    },
    {
        box_id: 11,
        image_1: "scooteringClothing-sq-1.jpg",
        image_2: "scooteringClothing-sq-2.jpg"
    },
]

function Box({ lightBoxDisplay, setLightBoxDisplay }) {
    const handleClick = () => {
        setLightBoxDisplay(false)
    }
    return (
        <div className="boxContainer">
            {boxImages.map(img => (
                <div className="box">
                    <img src={`/imgs/boxImages/${img.image_1}`} alt="" className="square_1" onClick={handleClick} />
                    <img src={`/imgs/boxImages/${img.image_2}`} alt="" className="square_2" />
                </div>
            ))
            }
        </div>
    )
}

export default Box
