import './home.css'
import { useState } from 'react';
import Hamburger from 'hamburger-react'
import Sidebar from '../../sidebar/Sidebar';

import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, Container, Offcanvas, NavDropdown, Form, FormControl, Button } from 'react-bootstrap';
// import mainSlide from "../../../imgs/main-slide-2.png"
import Particles from 'react-particles-js';

function Home() {
    const [isOpen, setOpen] = useState(false)
    return (
        <div className="home">
            <div className="mobileHeader">
                <Hamburger toggled={isOpen} toggle={setOpen} onClick={() => { isOpen ?? false }} />
            </div>
            <Particles className="particles" />
            <div className="slideContainer">
                <img className="mainSlide" src="imgs/main-slide-2.png" alt="" />
                <img className="mobileMainSlide" src="imgs/main-slide-mobile-1.png" alt="" />
                <h1 className="mainName">Emad Saeed</h1>
                <h2 className="mainTitle">Full Stack Developer</h2>
                <h1 className="slogan">Get the idea done!<br />from concept to implementation</h1>
            </div>
        </div >
    )
}

export default Home
